import React, { useState, useEffect } from "react";
import "./Home.css";

const Home = () => {
  const [ch, setCh] = useState("Zakaria");
  var direction = "toLeft";
  var i = 0;
  const chaine = () => {
    console.log(ch);
    setTimeout(() => {
      chaine();
    }, 100);
    setCh(ch.substring(0, i));
    if (direction === "toLeft") {
      i++;
      if (i > ch.length) direction = "toRight";
    } else {
      i--;
      if (i < 0) {
        direction = "toLeft";
      }
    }
    console.log("yes");
  };
  useEffect(() => {
    chaine();
    //return () => clearTimeout(timer);
  }, []);
  return (
    <div id="home">
      <h1>Hi, im {ch}</h1>
    </div>
  );
};

export default Home;
