import react, { useState } from "react";
import "./PourceBar.css";

const PourceBar = (props) => {
    let [WIDTH, setWidth] = useState("100px");
    let i = 0;
    const addWidth = () => {
        setWidth("200px");
    }
    return(
        <div onClick={addWidth} id="upper">
            <div id="lower" style={{width : WIDTH}}></div>
        </div>
    );
}

export default PourceBar;
