import React, { useState } from "react";
import "./Home.css";

const Home = () => {
    let swtch = true;
  const [ch, setCh] = useState(swtch ? "Zakaria":"hamid");
  var direction = "toLeft";
  var i = 0;
  const chaine = () => {
    console.log(ch);
    setCh(ch.substring(0, i));
    setTimeout(() => {
      chaine();
    }, 100);
    if (direction === "toLeft") {
      i++;
      if (i > ch.length) direction = "toRight";
    } else {
      i--;
      if (i < 0) {
        direction = "toLeft";
      }
    }
    console.log("yes");
    swtch = false;
  };
  return (
    <div id="home" onLoad={() => {chaine();}}>
      <h1>Hi, im {ch}</h1>
    </div>
  );
};

export default Home;
