import react, { useEffect, useState } from "react";
import "./PourceBar.css";

const PourceBar = (props) => {
    let [WIDTH, setWidth] = useState("0%");
    let i = 0;
    useEffect(() => {
        setWidth(`${props.pourcentage}`+"%");
    });
    return(
        <div id="upper">
            <p>{props.language}</p>
            <div id="lower" style={{width : WIDTH}}></div>
        </div>
    );
}

export default PourceBar;
