import React, { useState } from "react";
import "./Home.css";

const Home = () => {
    const [ch, setCh] = useState("Zakaria");
    var direction = "toLeft";
    var i = 0;
    const chaine = () => {
        console.log(ch);
        setCh((ch) => ch.substring(0, i));
        let timer = setTimeout(()=>{chaine();}, 100);
        if(direction === "toLeft"){
            i++;
            if(i>ch.length)
                direction = "toRight";
        }
        else{
            i--;
            if(i<0){
                direction="toLeft";
                setCh((ch) => "kakashi"); 
            }   	
        }
        console.log("yes");
    }
    const onClickHandler = () => {
        chaine();
    }
    return(
        <div id="home" onClick={onClickHandler}>
            <h1>Hi, im {ch}</h1>
        </div>
    );
}

export default Home;
