import "./Home.css";

const Home = () => {
    var[ch, setCh] = useState('');
  var direction = "toLeft";
  var i = 0;
  var swtch = true;
  const chaine = () => {
    setTimeout(() => {
      chaine();
    }, 100);
    setCh(swtch ? "Zakaria Dani":"a Software Engineer");
    console.log(ch);
    setCh((ch) => ch.substring(0, i));
    if (direction === "toLeft") {
      i++;
      if (i > ch.length) direction = "toRight";
    } else {
      i--;
      if (i < 0) {
        direction = "toLeft";
        swtch ? swtch=false : swtch=true;
      }
    }
    console.log("yes");
  };
  useEffect(() => {
    chaine();
    //return () => clearTimeout(timer);
  }, []);
  return (
    <div id="home">
      <h1>Hi, im {ch}</h1>
    </div>
  );
};

export default Home;
