  var [ch, setCh] = useState("");
  var direction = "toLeft";
  var i = 0;
  var swtch = true;
  const chaine = (event) => {
    setTimeout(() => {
      chaine();
    }, 100);
    setCh(swtch ? "Zakaria Dani" : "a Software Engineer");
    console.log(ch);
    setCh(ch.substring(0, i));
    if (direction === "toLeft") {
      i++;
      if (i > event.target.value.length) direction = "toRight";
    } else {
      i--;
      if (i < 0) {
        direction = "toLeft";
        swtch ? (swtch = false) : (swtch = true);
      }
    }
    console.log("yes");
  };
  useEffect(() => {
    chaine();
    //return () => clearTimeout(timer);
  }, []);
  return (
    <div id="home">
      <input value={`Hi, im ${ch}`}/>
    </div>
  );
};

export default Home;
