import React, { useState } from "react";
import "./Home.css";

const Home = () => {
    let [ch, setCh] = useState("Zakaria");
    useEffect(() => {
        const timer = setTimeout(() => {
          console.log('This will run after 1 second!')
        }, 1000);
        return () => clearTimeout(timer);
      }, []);
    var direction = "toLeft";
    var i = 0;
    const chaine = () => {
        console.log(ch);
        setCh(ch.substring(0, i));
        if(direction === "toLeft"){
            i++;
            if(i>ch.length)
                direction = "toRight";
        }
        else{
            i--;
            if(i<0){
                direction="toLeft";
            }   		
        }
        setTimeout(()=>{chaine();}, 100);
        console.log("yes");
    }
    const onClickHandler = () => {
        chaine();
    }
    return(
        <div id="home" onClick={onClickHandler}>
            <h1>Hi, im {ch}</h1>
        </div>
    );
}

export default Home;
