import React, { useState, useEffect } from "react";
import { Link, Outlet, useLocation } from "react-router-dom";
import "./NavBar.css";

const NavBar = () => {
  const [selected, setSelected] = useState([true,,,]);
  useEffect(() => {
    setSelected(JSON.parse(window.localStorage.getItem('selected')));
  }, []);
  useEffect(() => {
    window.localStorage.setItem('selected', selected);
  }, [selected]);
  return (
    <>
    <div className="navigation" >
      <ul>
        <li
          className={`list ${selected[0] ? "active" : ""}`}
          onClick={() => {
            setSelected([true]);
          }}
        >
          <Link to="/">
            <span className="icon">
              <ion-icon name="home-outline"></ion-icon>
            </span>
            <span className="text">Home</span>
          </Link>
        </li>
        <li
          className={`list ${selected[1] ? "active" : ""}`}
          onClick={() => {
            setSelected([false, true]);
          }}
        >
          <Link to="/about">
            <span className="icon">
              <ion-icon name="alert-circle-outline"></ion-icon>
            </span>
            <span className="text">About</span>
          </Link>
        </li>
        <li
          className={`list ${selected[2] ? "active" : ""}`}
          onClick={() => {
            setSelected([false, , true]);
          }}
        >
          <Link to="/skills" >
            <span className="icon">
              <ion-icon name="cog-outline"></ion-icon>
            </span>
            <span className="text">Skills</span>
          </Link>
        </li>
        <li
          className={`list ${selected[3] ? "active" : ""}`}
          onClick={() => {
            setSelected([false, , , true]);
          }}
        >
          <Link to="/contacts">
            <span className="icon">
              <ion-icon name="chatbubbles-outline"></ion-icon>
            </span>
            <span className="text">Contact</span>
          </Link>
        </li>
        <div className="indicator"></div>
      </ul>
    </div>
    <Outlet />
    </>
  );
};
export default NavBar;
